sys_script_include.script:
var SprinklrConfig = Class.create();
SprinklrConfig.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
getConfig : function (){
		return {
			subDomain : gs.getProperty('x_spri3_sprinklr.subdomain'),
			partnerId : gs.getProperty('x_spri3_sprinklr.partnerId'),
		};
	},

	subDomain : function(){
		return gs.getProperty('x_spri3_sprinklr.subdomain');
	},
	partnerId : function(){
		return gs.getProperty('x_spri3_sprinklr.partnerId');
	},
    type: 'SprinklrConfig'
});
