sys_widgets.script:
function sections() {
    return {
        'widget_name': { 'sprinklr_iframe' : 'sprinklr_iframe'},
        'widget_name2': {'sprinklr_iframe': 'sprinklr_iframe'}
    };
}

function render() {
    var uiPageName = renderer.getPreferences().get("sprinklr_iframe");
    return renderer.getRenderedPage(sprinklr_iframe);
}

function getEditLink() {
    var uiPageName = renderer.getPreference('sprinklr_iframe');
    return 'sys_ui_page.do?sysparm_query=' + encodeURIComponent('name=' + sprinklr_iframe);
}
